    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_group_image);
        context = this;
        gvImages = (GridView) findViewById(R.id.gv_images);
        GroupAdapter adapter = new GroupAdapter();
        gvImages.setAdapter(adapter);
        gvImages.setOnItemClickListener(new AdapterView.OnItemClickListener() {
            @Override
            public void onItemClick(AdapterView<?> parent, View view, int position, long id) {
                new BigImageBuilder((Activity) context).setImages(imageList)
                        .setOldImage(wrapOriginImageViewList())
                        .setPlaceHolderUrl(plackhoolder)
                        .setCurrentIndex(position)
                        .setStartPosition(gvImages.getFirstVisiblePosition())
                        .show();
            }
        });
    }

    /**
     *  ImageView 
     *
     * @return
     */
    private List<ImageView> wrapOriginImageViewList() {
        List<ImageView> originImgList = new ArrayList<>();
        for (int i = 0; i < gvImages.getChildCount(); i++) {
            ImageView thumImg = (ImageView) ((LinearLayout) gvImages.getChildAt(i)).getChildAt(0);
            originImgList.add(thumImg);
        }
        return originImgList;
    }

    private class GroupAdapter extends BaseAdapter {

        @Override
        public int getCount() {
            return plackhoolder.size();
        }

        @Override
        public String getItem(int position) {
            return plackhoolder.get(position);
        }

        @Override
        public long getItemId(int position) {
            return position;
        }

        @Override
        public View getView(int position, View convertView, ViewGroup parent) {
            ViewHolder viewHolder;
            if (convertView == null) {
                convertView = LayoutInflater.from(context).inflate(R.layout.item_image, parent, false);
                viewHolder = new ViewHolder(convertView);
                convertView.setTag(viewHolder);
            } else {
                viewHolder = (ViewHolder) convertView.getTag();
            }
            viewHolder.initView(getItem(position));
            return convertView;
        }

        class ViewHolder {
            private ImageView image_view;

            public ViewHolder(View convertView) {
                image_view = (ImageView) convertView.findViewById(R.id.image_view);
            }

            public void initView(String item) {

                Picasso.with(context).load(item).placeholder(R.mipmap.ic_launcher).into(image_view);
            }
        }
    }
